import Axios, { AxiosInstance } from 'axios';

export class TendermintQueryClient {
  private _axios: AxiosInstance;

  constructor(baseUrl: string) {
    this._axios = Axios.create({
      baseURL: baseUrl,
      headers: {
        Accept: 'application/json',
      },
      timeout: 15000,
    });
  }

  public async queryEstimateGas(hexTx: string) : Promise<number> {

    /*https://imperium-lcd.firmachain.org:26657/abci_query?path=%22app/simulate%22& */

    let path = "/abci_query?path=\"app/simulate\"";
    var result = await this._axios.get(path, { params: {data: hexTx } });

    let data = result.data.result.response.value;
    let jsonString = Buffer.from(data, "base64").toString('utf8');

    let jsonData = JSON.parse(jsonString);

    return Number.parseInt(jsonData.gas_info.gas_used);
  }
}

